--
-- 商品部分
--

DROP TABLE IF EXISTS `tag_mapping`;
DROP TABLE IF EXISTS `commodity`;
DROP TABLE IF EXISTS `brand`;
DROP TABLE IF EXISTS `category`;
DROP TABLE IF EXISTS `tag`;

CREATE TABLE `brand` (
  `brand_id` int unsigned PRIMARY KEY AUTO_INCREMENT,
  `brand_name` varchar(100)
) ENGINE = InnoDB AUTO_INCREMENT = 71 CHARACTER SET = utf8 COLLATE = utf8_general_ci ROW_FORMAT = Dynamic;



CREATE TABLE `category` (
  `category_id` int unsigned PRIMARY KEY AUTO_INCREMENT,
  `category_name` varchar(30)
) ENGINE = InnoDB AUTO_INCREMENT = 18 CHARACTER SET = utf8 COLLATE = utf8_general_ci ROW_FORMAT = Dynamic;


CREATE TABLE `tag` (
  `tag_id` int unsigned PRIMARY KEY AUTO_INCREMENT,
  `tag_name` varchar(30)
) ENGINE = InnoDB AUTO_INCREMENT = 33 CHARACTER SET = utf8 COLLATE = utf8_general_ci ROW_FORMAT = Dynamic;


CREATE TABLE `commodity` (
  `commodity_id` int unsigned PRIMARY KEY AUTO_INCREMENT,
  `commodity_name` varchar(200) DEFAULT '测试商品',
  `commodity_price` float DEFAULT 9.9,
  `commodity_cover` varchar(400) DEFAULT '',
  `commodity_tag` json,
  `commodity_sku` varchar(200),
  `commodity_origin_country` varchar(100),
  `commodity_note` varchar(200) DEFAULT '',
  `commodity_special_note` varchar(200) DEFAULT '',
  `commodity_hot` int unsigned DEFAULT 0,
  `commodity_weight` smallint unsigned DEFAULT 0,
  `commodity_create_time` timestamp DEFAULT CURRENT_TIMESTAMP,
  `commodity_brand_id` int unsigned,
  `commodity_category_id` int unsigned,
  `commodity_stock` int unsigned DEFAULT 100,
  FOREIGN KEY (`commodity_brand_id`) REFERENCES `brand` (`brand_id`),
  FOREIGN KEY (`commodity_category_id`) REFERENCES `category` (`category_id`)
) ENGINE = InnoDB AUTO_INCREMENT = 0 CHARACTER SET = utf8 COLLATE = utf8_general_ci ROW_FORMAT = Dynamic;


CREATE TABLE `tag_mapping` (
  `tag_mapping_id` int unsigned PRIMARY KEY AUTO_INCREMENT,
  `tag_id` int unsigned,
  `commodity_id` int unsigned,
  FOREIGN KEY (`tag_id`) REFERENCES `tag` (`tag_id`) ON DELETE CASCADE,
  FOREIGN KEY (`commodity_id`) REFERENCES `commodity` (`commodity_id`) ON DELETE CASCADE
) ENGINE = InnoDB AUTO_INCREMENT = 639 CHARACTER SET = utf8 COLLATE = utf8_general_ci ROW_FORMAT = Dynamic;


--
-- 社区部分
--

DROP TABLE IF EXISTS `comment`;
DROP TABLE IF EXISTS `post`;
DROP TABLE IF EXISTS `user`;

CREATE TABLE `user` (
  `user_id` int unsigned PRIMARY KEY AUTO_INCREMENT,
  `openid` varchar(100) NOT NULL,
  `session_key` varchar(100) NOT NULL,
  `gender` tinyint DEFAULT 0,
  `phone` varchar(11) DEFAULT '',
  `nick_name` varchar(100) DEFAULT '',
  `avatar_url` varchar(200) DEFAULT '',
  `country` varchar(50),
  `province` varchar(50),
  `city` varchar(50)
) ENGINE = InnoDB AUTO_INCREMENT = 6 CHARACTER SET = utf8 COLLATE = utf8_general_ci ROW_FORMAT = Dynamic;


CREATE TABLE `post` (
  `post_id` int unsigned PRIMARY KEY AUTO_INCREMENT,
  `post_title` varchar(200) NOT NULL,
  `post_content` text NOT NULL,
  `topic_id` int unsigned,
  `last_comment_time` timestamp,
  `create_time` timestamp DEFAULT now(),
  `post_owner_id` int unsigned NOT NULL,
  FOREIGN KEY (`post_owner_id`) REFERENCES `user` (`user_id`) ON DELETE CASCADE
) ENGINE = InnoDB AUTO_INCREMENT = 20 CHARACTER SET = utf8 COLLATE = utf8_general_ci ROW_FORMAT = Dynamic;


CREATE TABLE `comment` (
  `comment_id` int unsigned PRIMARY KEY AUTO_INCREMENT,
  `comment_content` text,
  `comment_owner_id` int unsigned,
  `post_id` int unsigned,
  `create_time` timestamp,
  FOREIGN KEY (`comment_owner_id`) REFERENCES `user` (`user_id`) ON DELETE CASCADE,
  FOREIGN KEY (`post_id`) REFERENCES `post` (`post_id`) ON DELETE CASCADE
) ENGINE = InnoDB AUTO_INCREMENT = 14 CHARACTER SET = utf8 COLLATE = utf8_general_ci ROW_FORMAT = Dynamic;

